package com.seluhadu.shchat;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.view.ViewPager;import android.support.v7.app.AppCompatActivity;import com.seluhadu.shchat.adapters.ChatViewPagerAdapter;import com.seluhadu.shchat.fragments.ChatFragment;import com.seluhadu.shchat.fragments.ChatUserProfileFragment;public class ChatActivity extends AppCompatActivity {    private ViewPager mViewPager;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_chat);        mViewPager = findViewById(R.id.chat_view_pager);        setUpViewPager();    }    private void setUpViewPager() {        Intent intent = getIntent();        String sender = intent.getStringExtra("senderId");        String receiver = intent.getStringExtra("receiverId");        String token = intent.getStringExtra("fireBaseToken");        ChatViewPagerAdapter adapter = new ChatViewPagerAdapter(getSupportFragmentManager());        adapter.addFragment(ChatFragment.newInstance(sender, receiver, token));        adapter.addFragment(ChatUserProfileFragment.newInstance(sender, receiver, token));        mViewPager.setAdapter(adapter);    }}