package com.seluhadu.shchat.adapters;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import java.util.List;public class ChatViewPagerAdapter extends FragmentPagerAdapter {    private List<Fragment> mListFragment;    public ChatViewPagerAdapter(FragmentManager fm) {        super(fm);    }    @Override    public Fragment getItem(int position) {        return mListFragment.get(position);    }    public void addFragment(Fragment fragment) {        mListFragment.add(fragment);    }    @Override    public int getCount() {        return mListFragment.size();    }}