package com.seluhadu.shchat.chat_interactore;import android.content.Context;import com.seluhadu.shchat.models.BaseMessage;public interface ChatContractor {    interface view {        void onSendMessageSuccess();        void onSendMessageFailure(String error);        void onGetMessageSuccess(BaseMessage message);        void onGetMessageFailure(String error);    }    interface presenter {        void sendMessage(Context context, BaseMessage baseMessage, String receiverFireBaseToken);        void getMessage(String senderId, String receiverId);    }    interface interact {        void sendMessageToFireBaseUser(Context context, BaseMessage baseMessage, String receiverFireBaseToken);        void getMessageToFireBaseUser(String senderId, String receiverId);    }    interface OnSendMessageListener {        void onSendMessageSuccess();        void onSendMessageFailure(String error);    }    interface OnGetMessagesListener {        void onGetMessagesSuccess(BaseMessage baseMessage);        void onGetMessagesFailure(String error);    }}