package com.seluhadu.shchat.chat_interactore;import android.content.Context;import com.seluhadu.shchat.models.BaseMessage;public class ChatPresenter implements ChatContractor.presenter, ChatContractor.OnGetMessagesListener, ChatContractor.OnSendMessageListener {    private ChatInteract interact;    private ChatContractor.view view;    public ChatPresenter(ChatContractor.view view) {        this.view = view;        this.interact = new ChatInteract(this, this);    }    @Override    public void sendMessage(Context context, BaseMessage baseMessage, String receiverFireBaseToken) {        interact.sendMessageToFireBaseUser(context, baseMessage, receiverFireBaseToken);    }    @Override    public void getMessage(String senderId, String receiverId) {        interact.getMessageToFireBaseUser(senderId, receiverId);    }    @Override    public void onSendMessageSuccess() {        view.onSendMessageSuccess();    }    @Override    public void onSendMessageFailure(String message) {        view.onSendMessageFailure(message);    }    @Override    public void onGetMessagesSuccess(BaseMessage baseMessage) {        view.onGetMessageSuccess();    }    @Override    public void onGetMessagesFailure(String message) {        view.onGetMessageFailure(message);    }}