package com.seluhadu.shchat.dialogs;import android.app.Dialog;import android.content.DialogInterface;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.design.widget.BottomSheetBehavior;import android.support.design.widget.BottomSheetDialog;import android.support.design.widget.BottomSheetDialogFragment;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.FrameLayout;import android.widget.TextView;import com.seluhadu.shchat.R;import de.hdodenhof.circleimageview.CircleImageView;public class DialogComment extends BottomSheetDialogFragment {    public DialogComment() {    }    public static DialogComment newInstance() {        Bundle args = new Bundle();        DialogComment fragment = new DialogComment();        fragment.setArguments(args);        return fragment;    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View rootView = inflater.inflate(R.layout.dialog_comment, container, false);        CircleImageView imageProfile = rootView.findViewById(R.id.user_profile);        TextView userName = rootView.findViewById(R.id.user_name);        TextView date_and_time = rootView.findViewById(R.id.date_and_time);        RecyclerView recyclerView = rootView.findViewById(R.id.comment_recycler_view);        return rootView;    }    @NonNull    @Override    public Dialog onCreateDialog(Bundle savedInstanceState) {        final BottomSheetDialog dialogFragment = (BottomSheetDialog) super.onCreateDialog(savedInstanceState);        dialogFragment.setOnShowListener(new DialogInterface.OnShowListener() {            @Override            public void onShow(DialogInterface dialog) {                BottomSheetDialog d = (BottomSheetDialog) dialog;                FrameLayout bottomSheet = d.findViewById(android.support.design.R.id.design_bottom_sheet);                assert bottomSheet != null;                BottomSheetBehavior.from(bottomSheet).setState(BottomSheetBehavior.STATE_EXPANDED);            }        });        return dialogFragment;    }    void test(){    }}