package com.seluhadu.shchat.models;public abstract class BaseMessage {    private long mMessageId;    private long mCreatedAt;    private long mUpdatedAt;    private String userId;    public BaseMessage(long mMessageId, long mCreatedAt, long mUpdatedAt, String userId) {        this.mMessageId = mMessageId;        this.mCreatedAt = mCreatedAt;        this.mUpdatedAt = mUpdatedAt;        this.userId = userId;    }    public long getmMessageId() {        return mMessageId;    }    public void setmMessageId(long mMessageId) {        this.mMessageId = mMessageId;    }    public long getmCreatedAt() {        return mCreatedAt;    }    public void setmCreatedAt(long mCreatedAt) {        this.mCreatedAt = mCreatedAt;    }    public long getmUpdatedAt() {        return mUpdatedAt;    }    public void setmUpdatedAt(long mUpdatedAt) {        this.mUpdatedAt = mUpdatedAt;    }    public String getUserId() {        return userId;    }    public void setUserId(String userId) {        this.userId = userId;    }}