package com.seluhadu.shchat.models;import org.json.JSONException;import org.json.JSONObject;public class UrlPreview {    private String url;    private String name;    private String title;    private String description;    private String imageUrl;    public UrlPreview() {    }    public UrlPreview(String url, String name, String title, String description, String imageUrl) {        this.url = url;        this.name = name;        this.title = title;        this.description = description;        this.imageUrl = imageUrl;    }    public UrlPreview(String jsonString) throws JSONException {        JSONObject jsonObject = new JSONObject(jsonString);        url = jsonObject.getString("url");        name = jsonObject.getString("name");        title = jsonObject.getString("title");        description = jsonObject.getString("description");        imageUrl = jsonObject.getString("imageUrl");    }    public String toJsonString() throws JSONException {        JSONObject jsonObject = new JSONObject();        jsonObject.put("url", url);        jsonObject.put("name", name);        jsonObject.put("title", title);        jsonObject.put("description", description);        jsonObject.put("imageUrl", imageUrl);        return jsonObject.toString();    }    public String getUrl() {        return url;    }    public void setUrl(String url) {        this.url = url;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public String getImageUrl() {        return imageUrl;    }    public void setImageUrl(String imageUrl) {        this.imageUrl = imageUrl;    }}