package com.seluhadu.shchat.models;import android.os.Parcel;import java.util.HashMap;public class UserMessage extends BaseMessage {    private String message = "";    public UserMessage() {        super();    }    protected UserMessage(Parcel in) {        super(in);        message = in.readString();    }    public static HashMap<String, Object> build(String userId, String receiverId, String message, long createdAt, long updatedAt, long messageId, long newDate) {        HashMap<String, Object> obj = new HashMap<>();        obj.put("userId", userId);        obj.put("receiverId", receiverId);        obj.put("message", message);        obj.put("msgType", "UMSG");        obj.put("createdAt", createdAt);        obj.put("messageId", messageId);        obj.put("updatedAt", updatedAt);        obj.put("newDate", newDate);        return obj;    }    public UserMessage(HashMap<String, Object> obj) {        super(obj);        this.message = obj.get("message").toString();    }    HashMap<String, Object> toJson() {        HashMap<String, Object> obj = super.toJson();        obj.put("msgType", "MESG");        obj.put("data", "data");        obj.put("customType", "customType");        obj.put("message", this.message);        return obj;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public static final Creator<UserMessage> CREATOR = new Creator<UserMessage>() {        @Override        public UserMessage createFromParcel(Parcel in) {            return new UserMessage(in);        }        @Override        public UserMessage[] newArray(int size) {            return new UserMessage[size];        }    };    @Override    public void writeToParcel(Parcel dest, int flags) {        super.writeToParcel(dest, flags);        dest.writeString(message);    }}