package com.seluhadu.shchat.models;import android.support.annotation.IntDef;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;public class UserMode {    @Retention(RetentionPolicy.SOURCE)    @IntDef({NON_AVAILABLE, ONLINE, OFFLINE})    @interface userMode {}    public void setMode(@userMode int mode){}    @userMode    public int getMode(){        return ONLINE;    }    public static final int NON_AVAILABLE = 0;    public static final int ONLINE = 1;    public static final int OFFLINE = 2;}